"use strict";(self.webpackChunksap_architecture_center=self.webpackChunksap_architecture_center||[]).push([[2311],{41438:e=>{e.exports=JSON.parse('{"tag":{"label":"Data & Analytics","permalink":"/@preview/jmsrpp-content-bdc-ra0011/tags/data","description":"Access insights wherever your data resides with SAP data and analytics solutions.","allTagsPath":"/@preview/jmsrpp-content-bdc-ra0011/tags","count":13,"items":[{"id":"ref-arch/RA0004/id-ra0004","title":"Explore your Hyperscaler data with SAP Datasphere","description":"SAP Datasphere\'s data integration architectures that harmonizes SAP and non-SAP data using data fabric architecture helps business experts to make data-driven decisions with unprecedented impact.","permalink":"/@preview/jmsrpp-content-bdc-ra0011/ref-arch/28ac36db13"},{"id":"ref-arch/RA0003/id-ra0003","title":"Federated Machine Learning with SAP Datasphere","description":"The SAP Federated Machine Learning Python library (FedML) applies the data federation architecture of SAP Datasphere for intelligently sourcing SAP and non-SAP data for Machine Learning experiments, run on any Machine Learning platform, thereby removing the need for replication or data movement. By abstracting data connection, data discovery, data loading (for all ML platforms), model training, model deployment, and inferencing (for hyperscaler machine learning platforms), the FedML library offers end-to-end integration with just a few lines of code.","permalink":"/@preview/jmsrpp-content-bdc-ra0011/ref-arch/785c72ab48"},{"id":"ref-arch/RA0003/fedml-watsonx/id-ra0003-6","title":"FedML and IBM watsonx.ai / IBM Watson Studio integration","description":"FedML\'s IBM watsonx support helps data scientists accelerate machine learning workflows with IBM watsonx workflows, while providing instant access to SAP\'s critical business data thereby eliminating the need to duplicate data for model training.","permalink":"/@preview/jmsrpp-content-bdc-ra0011/ref-arch/785c72ab48/6"},{"id":"ref-arch/RA0003/fedml-aws/id-ra0003-1","title":"FedML-AWS for Amazon Sagemaker","description":"FedML-AWS provides end-to-end integraton for training models in Amazon Sagemaker using live business data from SAP systems and eliminates the need for duplicating the data.","permalink":"/@preview/jmsrpp-content-bdc-ra0011/ref-arch/785c72ab48/1"},{"id":"ref-arch/RA0003/fedml-azure/id-ra0003-2","title":"FedML-Azure for Azure Machine Learning","description":"FedML-Azure provides end-to-end integraton for training models in Azure Machine Learning service, using live business data from SAP systems and eliminates the need for duplicating the data.","permalink":"/@preview/jmsrpp-content-bdc-ra0011/ref-arch/785c72ab48/2"},{"id":"ref-arch/RA0003/fedml-databricks/id-ra0003-3","title":"FedML-Databricks for Databricks platform","description":"FedML-Databricks provides end-to-end integraton for training models in Databricks machine learning platform, using live business data from SAP systems and eliminates the need for duplicating the data.","permalink":"/@preview/jmsrpp-content-bdc-ra0011/ref-arch/785c72ab48/3"},{"id":"ref-arch/RA0003/fedml-gcp/id-ra0003-4","title":"FedML-GCP for Google Vertex AI","description":"FedML-GCP provides end-to-end integraton for training models in Google Vertex AI using live business data from SAP systems and eliminates the need for duplicating the data.","permalink":"/@preview/jmsrpp-content-bdc-ra0011/ref-arch/785c72ab48/4"},{"id":"ref-arch/RA0003/fedml-nvidia/id-ra0003-5","title":"FedML\'s support for NVIDIA GPUs","description":"FedML now supports reading of federated SAP business data via SAP Datasphere directly into NVIDIA GPU environment computes for model training.","permalink":"/@preview/jmsrpp-content-bdc-ra0011/ref-arch/785c72ab48/5"},{"id":"ref-arch/RA0004/aws-data-integration/id-ra0004-1","title":"Integration with AWS data sources","description":"Data from AWS data sources can be harmonized with SAP and non-sap data via SAP Datasphere\'s data fabric architecture.","permalink":"/@preview/jmsrpp-content-bdc-ra0011/ref-arch/28ac36db13/1"},{"id":"ref-arch/RA0004/azure-data-integration/id-ra0004-2","title":"Integration with Azure data sources","description":"Data from Azure platform data services can be seamlessly integrated and harmonized with both SAP and non-SAP data using SAP Datasphere\'s robust data fabric architecture. This architecture facilitates the unification of disparate data sources, enabling comprehensive data modeling and analytics.","permalink":"/@preview/jmsrpp-content-bdc-ra0011/ref-arch/28ac36db13/2"},{"id":"ref-arch/RA0004/databricks-data-integration/id-ra0004-3","title":"Integration with Databricks","description":"Data from Databricks Lakehouse can be harmonized with SAP and non-sap data via SAP Datasphere\'s unified data models for use with richer analytics and other use cases.","permalink":"/@preview/jmsrpp-content-bdc-ra0011/ref-arch/28ac36db13/3"},{"id":"ref-arch/RA0004/gcp-data-integration/id-ra0004-4","title":"Integration with Google Cloud Platform sources","description":"Data from Google Cloud Platform (GCP) data services can be seamlessly integrated and harmonized with both SAP and non-SAP data using SAP Datasphere\'s advanced data fabric architecture.","permalink":"/@preview/jmsrpp-content-bdc-ra0011/ref-arch/28ac36db13/4"},{"id":"ref-arch/RA0004/latency-performance-considerations/id-ra0004-5","title":"Latency and Performance considerations","description":"In data federation scenarios, particularly where non-SAP and SAP data are virtually queried to avoid duplication, performance and latency are critical factors that influence the effectiveness of the data federation architecture for analytics solutions","permalink":"/@preview/jmsrpp-content-bdc-ra0011/ref-arch/28ac36db13/5"}],"unlisted":false}}')}}]);