"use strict";(self.webpackChunksap_architecture_center=self.webpackChunksap_architecture_center||[]).push([[922],{55522:e=>{e.exports=JSON.parse('{"tag":{"label":"Generative AI","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/tags/genai","description":"Generative AI is a type of AI that is capable of generating new data, such as images, videos, and text, based on the data it has been trained on.","allTagsPath":"/@preview/johannagonnzdz-adjust-images-in-md/docs/tags","count":16,"items":[{"id":"ref-arch/RA0005/ai-agents/id-ra0005-5","title":"AI Agents & Project Agent Builder (PAB)","description":"This is an overview of AI agents and their role in enterprise automation, focusing on SAP\'s Project Agent Builder (PAB) and its support for content-based and code-based agent development. It explores the technical components, design strategies, and evaluation criteria for deploying AI agents effectively within the SAP ecosystem.","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/e5eb3b9b1d/5"},{"id":"ref-arch/RA0007/mt-authentication/id-ra0007-6","title":"Authentication Strategies","description":"Authentication Strategies","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/d31bedf420/6"},{"id":"ref-arch/RA0005/basics/id-ra0005-1","title":"Basic Prompting","description":"Enhance SAP BTP applications using Generative AI by integrating custom prompts for Foundation Models or LLMs. This allows natural language processing to craft AI responses tailored to specific tasks, enabling intelligent user interactions and achieving application objectives.","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/e5eb3b9b1d/1"},{"id":"ref-arch/RA0007/mt-benefits/id-ra0007-1","title":"Benefits of Multitenant Applications","description":"Benefits of Multitenant Applications","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/d31bedf420/1"},{"id":"ref-arch/RA0007/mt-tco/id-ra0007-5","title":"Cost of Ownership","description":"Cost of Ownership","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/d31bedf420/5"},{"id":"ref-arch/RA0001/design-considerations/id-ra0001-2","title":"Design Considerations for EDA Applications","description":"Understand key challenges, key design patterns and key considerations from platform, technical services,when building event-driven architecture based applications.","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/fbdc46aaae/2"},{"id":"ref-arch/RA0005/semantic-search/id-ra0005-2","title":"Embeddings & Semantic Search","description":"Semantic search using embeddings converts data into dense numeric vectors to capture meanings. Stored in vector databases, these vectors enable efficient similarity searches with cosine similarity, improving search relevance and user experience by focusing on meaning over exact keywords.","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/e5eb3b9b1d/2"},{"id":"ref-arch/RA0005/id-ra0005","title":"Generative AI on SAP BTP","description":"Harness Generative AI on SAP BTP using a CAP-based backend with SAP HANA Cloud\'s Vector Engine for similarity search. The Generative AI Hub allows seamless integration of LLMs, supporting RAG and other advanced patterns. Compatible with Cloud Foundry and Kyma, it optimizes AI-driven app development.","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/e5eb3b9b1d"},{"id":"ref-arch/RA0005/intelligent-data-apps/id-ra0005-6","title":"Intelligent Data Apps","description":"Intelligent Data Apps leverage advanced AI capabilities to transform how organizations interact with and derive insights from large quantities of structured data. By integrating natural language processing, federated data access, and real-time analytics, these applications enable seamless decision-making and operational efficiency across diverse industries.","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/e5eb3b9b1d/6"},{"id":"ref-arch/RA0001/event-driven-arch/id-ra0001-1","title":"Introduction and SAP\'s EDA Strategy","description":"Understanding Event-Driven Architecture, Core Concepts and Building Blocks.","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/fbdc46aaae/1"},{"id":"ref-arch/RA0007/id-ra0007","title":"Multitenant SaaS Application using CAP","description":"Multitenant SaaS applications built on SAP BTP offer a powerful way to extend SAP\'s core solutions and address specific industry or business needs at scale. By allowing multiple customers to share a single instance of the software, multitenancy lowers costs and makes maintenance simple for all users. This approach not only drives innovation and scalability but also enables faster time-to-market and more efficient resource utilization.","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/d31bedf420"},{"id":"ref-arch/RA0007/mt-architecture/id-ra0007-4","title":"Reference Architecture","description":"Reference Architecture","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/d31bedf420/4"},{"id":"ref-arch/RA0001/id-ra0001","title":"Reference Architecture for Event-Driven Applications","description":"Guidance for developing applications based on Event-Driven Architecture (EDA) patterns and Cloud Application Programming (CAP) framework.","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/fbdc46aaae"},{"id":"ref-arch/RA0005/retrieval-augmented-generation/id-ra0005-3","title":"Retrieval Augmented Generation (RAG)","description":"Enhance LLM control with Retrieval Augmented Generation (RAG), which integrates external data to reduce hallucinations and improve accuracy across multiple domains without the need for fine-tuning. This approach combines factual data with language knowledge for increased precision.","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/e5eb3b9b1d/3"},{"id":"ref-arch/RA0007/mt-lifecycle/id-ra0007-3","title":"Tenant Lifecycle Management","description":"Tenant Lifecycle Management","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/d31bedf420/3"},{"id":"ref-arch/RA0007/mt-models/id-ra0007-2","title":"Tenant Model on SAP BTP","description":"Tenant Model on SAP BTP","permalink":"/@preview/johannagonnzdz-adjust-images-in-md/docs/ref-arch/d31bedf420/2"}],"unlisted":false}}')}}]);