"use strict";(self.webpackChunksap_architecture_center=self.webpackChunksap_architecture_center||[]).push([[20114],{84629:e=>{e.exports=JSON.parse('{"tag":{"label":"Event-Driven Architecture","permalink":"/@preview/vedant-aero-ml-dev/docs/tags/eda","description":"An event-driven architecture (EDA) is an integration model built to publish, capture, process, and respond to events across distributed systems in real time. When an event occurs in one application, a message is automatically sent to all the other applications that need to know about it, so they can act on it in turn.","allTagsPath":"/@preview/vedant-aero-ml-dev/docs/tags","count":6,"items":[{"id":"ref-arch/RA0001/aws-iot-integration/id-ra0001-3","title":"Build Events-to-Business Actions Scenarios with SAP BTP and AWS IoT Core","description":"Sample application for building event-driven architecture application with AWS IoT as event source. Assess the critical events and publish to SAP BTP for validation and processing the business processes operations in SAP S/4HANA.","permalink":"/@preview/vedant-aero-ml-dev/docs/ref-arch/fbdc46aaae/3"},{"id":"ref-arch/RA0001/azure-iot-integration/id-ra0001-4","title":"Build Events-to-Business Actions Scenarios with SAP BTP and Microsoft Azure","description":"Sample application for building event-driven architecture application with Microsoft Azure IoT as event source. Assess the critical events and publish to SAP BTP for validation and processing the business processes operations in SAP S/4HANA.","permalink":"/@preview/vedant-aero-ml-dev/docs/ref-arch/fbdc46aaae/4"},{"id":"ref-arch/RA0001/design-considerations/id-ra0001-2","title":"Design Considerations for EDA Applications","description":"Understand key challenges, key design patterns and key considerations from platform, technical services,when building event-driven architecture based applications.","permalink":"/@preview/vedant-aero-ml-dev/docs/ref-arch/fbdc46aaae/2"},{"id":"ref-arch/RA0002/event-synchronization/id-ra0002-5","title":"Events Synchronization","description":"Event synchronization in multi-region setups is enabled by SAP\'s Advanced Event Mesh (AEM) with Dynamic Message Routing (DMR), ensuring real-time, resilient, and scalable event processing across regions, maintaining continuity and high availability during infrastructure failures.","permalink":"/@preview/vedant-aero-ml-dev/docs/ref-arch/81805673c0/5"},{"id":"ref-arch/RA0001/event-driven-arch/id-ra0001-1","title":"Introduction and SAP\'s EDA Strategy","description":"Understanding Event-Driven Architecture, Core Concepts and Building Blocks.","permalink":"/@preview/vedant-aero-ml-dev/docs/ref-arch/fbdc46aaae/1"},{"id":"ref-arch/RA0001/id-ra0001","title":"Reference Architecture for Event-Driven Applications","description":"Guidance for developing applications based on Event-Driven Architecture (EDA) patterns and Cloud Application Programming (CAP) framework.","permalink":"/@preview/vedant-aero-ml-dev/docs/ref-arch/fbdc46aaae"}],"unlisted":false}}')}}]);