"use strict";(self.webpackChunksap_architecture_center=self.webpackChunksap_architecture_center||[]).push([[78431],{79278:e=>{e.exports=JSON.parse('{"tag":{"label":"Integration","permalink":"/@preview/xammaxx-fix/173-footer/docs/tags/integration","description":"Connect and automate your processes business-wide.","allTagsPath":"/@preview/xammaxx-fix/173-footer/docs/tags","count":17,"items":[{"id":"ref-arch/RA0002/id-ra0002","title":"Architecting Multi-Region HA/DR resiliency patterns","description":"Learn how to architect multi-region resiliency for SAP solutions such to ensure high availability and business continuity. This guide covers strategies, best practices, and sample implementations for a robust, compliant multi-region setup with failover and disaster recovery capabilities.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/6c73e3575f"},{"id":"ref-arch/RA0001/aws-iot-integration/id-ra0001-3","title":"Build Events-to-Business Actions Scenarios with SAP BTP and AWS IoT Core","description":"Sample application for building event-driven architecture application with AWS IoT as event source. Assess the critical events and publish to SAP BTP for validation and processing the business processes operations in SAP S/4HANA.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/a06a959120/3"},{"id":"ref-arch/RA0001/azure-iot-integration/id-ra0001-4","title":"Build Events-to-Business Actions Scenarios with SAP BTP and Microsoft Azure","description":"Sample application for building event-driven architecture application with Microsoft Azure IoT as event source. Assess the critical events and publish to SAP BTP for validation and processing the business processes operations in SAP S/4HANA.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/a06a959120/4"},{"id":"ref-arch/RA0002/control-plane/id-ra0002-6","title":"Control Plane for Orchestration","description":"Multi-Region Manager (MRM) is an open-source CAP-based application that orchestrates multi-region processes like replication topology, failover management, load balancer control, and monitoring. It enables seamless failover between regions, ensuring business continuity and service availability.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/6c73e3575f/6"},{"id":"ref-arch/RA0002/tco/id-ra0002-7","title":"Cost of Ownership","description":"This document provides sample implementations using Azure Traffic Manager and AWS Route 53 for high availability and disaster recovery (HA/DR) of SAP services. It includes stateless and stateful setups for SAP Cloud Integration, SAP Work Zone, and SAP HANA Cloud with multi-region DR solutions.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/6c73e3575f/9"},{"id":"ref-arch/RA0002/data-synchronization/id-ra0002-4","title":"Data Synchronization","description":"Data synchronization in multi-region setups ensures consistency across locations. SAP HANA Cloud with Smart Data Access (SDA) enables efficient data replication and resiliency, providing real-time updates, failover capabilities, and robust data availability across regions.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/6c73e3575f/4"},{"id":"ref-arch/RA0001/design-considerations/id-ra0001-2","title":"Design Considerations for EDA Applications","description":"Understand key challenges, key design patterns and key considerations from platform, technical services,when building event-driven architecture based applications.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/a06a959120/2"},{"id":"ref-arch/RA0002/event-synchronization/id-ra0002-5","title":"Events Synchronization","description":"Event synchronization in multi-region setups is enabled by SAP\'s Advanced Event Mesh (AEM) with Dynamic Message Routing (DMR), ensuring real-time, resilient, and scalable event processing across regions, maintaining continuity and high availability during infrastructure failures.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/6c73e3575f/5"},{"id":"ref-arch/RA0002/geographic-redundancy/id-ra0002-2","title":"Geographic Redundancy","description":"Geographic redundancy ensures continuous service availability by distributing data and resources across multiple locations. It minimizes downtime, enables disaster recovery, and synchronizes critical components across regions for SAP BTP services and applications.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/6c73e3575f/2"},{"id":"ref-arch/RA0002/sample-implementations/id-ra0002-7","title":"HA/DR Sample Implementations","description":"This document provides sample implementations using Azure Traffic Manager and AWS Route 53 for high availability and disaster recovery (HA/DR) of SAP services. It includes stateless and stateful setups for SAP Cloud Integration, SAP Work Zone, and SAP HANA Cloud with multi-region DR solutions.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/6c73e3575f/7"},{"id":"ref-arch/RA0001/event-driven-arch/id-ra0001-1","title":"Introduction and SAP\'s EDA Strategy","description":"Understanding Event-Driven Architecture, Core Concepts and Building Blocks.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/a06a959120/1"},{"id":"ref-arch/RA0002/loadbalancers/id-ra0002-3","title":"Load Balancers","description":"Load balancers enhance reliability, scalability, and fault tolerance in multi-region SAP BTP setups by distributing traffic intelligently. They ensure optimal performance with real-time failover, health checks, and advanced features, providing seamless operations across diverse geographies.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/6c73e3575f/3"},{"id":"ref-arch/RA0002/fundamentals/multi-region-resiliency/id-ra0002-1-2","title":"Multi-region resiliency","description":"Multi-region resiliency ensures applications remain functional during regional outages by leveraging geographically distributed data centers. It enhances disaster recovery, minimizes downtime, reduces latency, ensures compliance, and provides a seamless global user experience.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/6c73e3575f/1/2"},{"id":"ref-arch/RA0001/id-ra0001","title":"Reference Architecture for Event-Driven Applications","description":"Guidance for developing applications based on Event-Driven Architecture (EDA) patterns and Cloud Application Programming (CAP) framework.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/a06a959120"},{"id":"ref-arch/RA0002/security/id-ra0002-7","title":"Security","description":"This document provides sample implementations using Azure Traffic Manager and AWS Route 53 for high availability and disaster recovery (HA/DR) of SAP services. It includes stateless and stateful setups for SAP Cloud Integration, SAP Work Zone, and SAP HANA Cloud with multi-region DR solutions.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/6c73e3575f/8"},{"id":"ref-arch/RA0002/fundamentals/id-ra0002-1","title":"Single-region and Multi-region resiliency","description":"Overview for Single and Multi-region resiliency","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/6c73e3575f/1"},{"id":"ref-arch/RA0002/fundamentals/single-region-resiliency/id-ra0002-1-1","title":"Single-region resiliency","description":"This guide explains single-region resiliency for SAP BTP using Availability Zones (AZs), ensuring high availability, low-latency replication, and failover within a region. While reliable, Multi-AZ setups are vulnerable to region-wide outages and may not meet global performance or compliance needs.","permalink":"/@preview/xammaxx-fix/173-footer/docs/ref-arch/6c73e3575f/1/1"}],"unlisted":false}}')}}]);